import { Component, Input } from '@angular/core';
import { FaIconLibrary } from '@fortawesome/angular-fontawesome';
import { fas } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.scss']
})
export class ButtonComponent {
  @Input() buttonText?: string;  // Input for button text (optional)
  @Input() iconName?: string;    // Input for icon name (optional)

  constructor(library: FaIconLibrary) {
    library.addIconPacks(fas); // Load FontAwesome solid icons
  }
}

  <div class="app-button" [ngClass]="{'app-button__single-icon': !buttonText}">
    <fa-icon *ngIf="iconName" [icon]="['fas', 'pencil']"></fa-icon>
    <span *ngIf="buttonText">{{ buttonText }}</span>
  </div>
