import { Component, OnInit } from "@angular/core";
import { CoursesService } from "../../services/courses.service";
import { UserStoreService } from "../../user/services/user-store.service";
import { Router } from "@angular/router";
import { Course } from "../../models"; // Adjust the import based on your file structure

@Component({
    selector: "app-courses",
    templateUrl: "./courses.component.html",
    styleUrls: ["./courses.component.scss"],
})
export class CoursesComponent implements OnInit {
    courses: Course[] = [];
    isAdmin: boolean = false;
    searchValue: string = "";

    constructor(
        private coursesService: CoursesService,
        private userStore: UserStoreService,
        private router: Router
    ) {}

    ngOnInit() {
        this.loadCourses();
        this.isAdmin = this.userStore.isAdmin; // Get admin status
    }

    loadCourses() {
        this.coursesService.getAll().subscribe((data: Course[]) => {
            this.courses = data;
        });
    }

    filterCourses() {
        this.coursesService
            .filterCourses(this.searchValue)
            .subscribe((filteredCourses) => {
                this.courses = filteredCourses;
            });
    }

    editCourse(courseId: string) {
        this.router.navigate(["/course/edit", courseId]);
    }

    showCourse(courseId: string) {
        this.router.navigate(["/course", courseId]);
    }

    deleteCourse(courseId: string) {
        this.coursesService.deleteCourse(courseId).subscribe(() => {
            this.loadCourses(); // Refresh the list after deletion
        });
    }
}
