import { Component, Input, out } from '@angular/core';

@Component({
  selector: 'app-courses-list',
  templateUrl: './courses-list.component.html',
  styleUrls: ['./courses-list.component.scss']
})
export class CoursesListComponent {
// Input for list of courses
@Input() courses: Course[] = [];

// Input for determining if courses are editable
@Input() editable: boolean = false;

// Output for showCourse action
@Output() showCourse = new EventEmitter<Course>();

// Output for editCourse action
@Output() editCourse = new EventEmitter<Course>();

// Output for deleteCourse action
@Output() deleteCourse = new EventEmitter<Course>();

// Emit showCourse event
onShowCourse(course: Course) {
  this.showCourse.emit(course);
}

// Emit editCourse event
onEditCourse(course: Course) {
  this.editCourse.emit(course);
}

// Emit deleteCourse event
onDeleteCourse(course: Course) {
  this.deleteCourse.emit(course);
}
}
